import './Expenses.css';
import ExpenseItem from '../Expenses/ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react/cjs/react.development';

const Expenses = (props) => {
  const [yearDefault, setYear] = useState('2021');

  const onFilterHandler = (year) => {
    console.log('expense filter');
    setYear('');
    setYear(year);
    // console.log(yearDefault);
  }
    return (
      <div>
        <Card className="expenses">
          <ExpensesFilter value={yearDefault} onFilter={onFilterHandler}/>
          
          <ExpenseItem
            name={props.expenses[0].name}
            date={props.expenses[0].date}
            amount={props.expenses[0].amount}
          />
          <ExpenseItem
            name={props.expenses[1].name}
            date={props.expenses[1].date}
            amount={props.expenses[1].amount}
          />
          <ExpenseItem
            name={props.expenses[2].name}
            date={props.expenses[2].date}
            amount={props.expenses[2].amount}
          />
          <ExpenseItem
            name={props.expenses[3].name}
            date={props.expenses[3].date}
            amount={props.expenses[3].amount}
          />
        </Card>
      </div>
    );
}

export default Expenses;
